//-- function
int8_t *init_matrix(param_t *P)
{
  int8_t *MAT;
  if (strcmp(P->submat_name,"blosum62")==0)      {  MAT = BLOSUM62; P->lambda = BLOSUM62_LAMBDA; P->K = BLOSUM62_K; }
  else if (strcmp(P->submat_name,"blosum90")==0) {  MAT = BLOSUM90; P->lambda = BLOSUM90_LAMBDA; P->K = BLOSUM90_K; }
  else if (strcmp(P->submat_name,"blosum80")==0) {  MAT = BLOSUM80; P->lambda = BLOSUM80_LAMBDA; P->K = BLOSUM80_K; }
  else if (strcmp(P->submat_name,"blosum50")==0) {  MAT = BLOSUM50; P->lambda = BLOSUM50_LAMBDA; P->K = BLOSUM50_K; }
  else if (strcmp(P->submat_name,"blosum45")==0) {  MAT = BLOSUM45; P->lambda = BLOSUM45_LAMBDA; P->K = BLOSUM45_K; }
  else if (strcmp(P->submat_name,"pam250")==0)   {  MAT = PAM250;   P->lambda = PAM250_LAMBDA;   P->K = PAM250_K;   }
  else if (strcmp(P->submat_name,"pam70")==0)    {  MAT = PAM70;    P->lambda = PAM70_LAMBDA;    P->K = PAM70_K;    }
  else if (strcmp(P->submat_name,"pam30")==0)    {  MAT = PAM30;    P->lambda = PAM30_LAMBDA;    P->K = PAM30_K;    }
  else
    {
      fprintf (stderr,"\nERROR: no matrix named %s available\nexit...\n\n",P->submat_name); exit (255);
    }

  int8_t *M = (int8_t *) malloc(sizeof(int8_t)*1024);
  for (int i=0; i<1024; i++) M[i] = MAT[i];
  return M;
}

//-- function
void free_matrix(int8_t *M)
{
  free (M);
}
